package onodes.RMI.Server;

import java.rmi.RemoteException;

import onodes.RMI.ControllerRMI;
import pnodes.monAppServer.ControllerMonAppServer;

/**
 *
 * DO NOT EDIT THIS FILE IT BELONG TO THE FRAMEWORK
 * 
 * @author groupe1
 *
 */

public class ControllerRMIServer extends ControllerRMI<ModelRMIServer, ViewRMIServer> {

	public ControllerRMIServer(ControllerMonAppServer cappserv) throws RemoteException {
		super();
		model=new ModelRMIServer(cappserv);
		view=new ViewRMIServer();
	}
	
	public ControllerRMIServer(ModelRMIServer model, ViewRMIServer view) throws RemoteException {
		super(model, view);
	}

	public void invokeMethodOnAllControllerAppClient(String methodName,
			Class[] args) {
		model.invokeMethodOnAllControllerAppClient(methodName, args);
	}
	
	public Object invokeMethodOnControllerAppClient (int idClient, String methodName,
			Class[] args) {
		return model.invokeMethodOnControllerAppClient(idClient, methodName, args);
	}
}
